     } else {                                alert('AE:' + typeof(data.result.msg) === 'undefined' ? JSON.stringify(data) : data.result.msg);                                cfg.callback(data);                            }                    } else {                        if (typeof cfg.callback == 'function') {                            cfg.callback(data);                        }                    }                };                cfg.beforeSend = function (xhr) {                    if (typeof cfg.loading != 'undefined')                        $.showLoading(cfg.loading);                    if (accessToken)                        xhr.setRequestHeader('X-AUTH-ACCESS-TOKEN', accessToken);                    xhr.setRequestHeader('X-CSRF-TOKEN', $("#csrfToken").attr('content'));                };                cfg.complete = function (xhr) {                    if (typeof cfg.loading != 'undefined')                        $.hideLoading();                    if (xhr.status != 200)                        alert('XHR状态非200');                };                $.ajax(cfg);            };        </script>    <?php endif; ?>    <?php if (!empty($output['sso_token'])):?>        <script>            var __sso_token = '<?=$output['sso_token']?>'        </script>    <?php endif;?>    <?php if (!empty(C('app_alias'))): ?>        <?php if ($output['sso_token']): ?>            <script>                var __show = function () {                    var times = 0;                    var __timer = setInterval(function (){                        times = times+1;                        if (document.readyState == 'complete') {                            _show_checkin();                            clearInterval(__timer);                        }                        if (times>100) {                            clearInterval(__timer);                        }                    }, 500);                }            </script>            <scrip