url,                        removeHeader: false                    })                    <?php if (in_array($output['platform'], ['ios', 'android'])):?>                    layer.open({                        content: '前往支付...',                        shadeClose: false,                        btn: ['支付成功', '遇到问题'],                        yes: function (index) {                            window.location.href = '<?=urlMobile('member_order', 'index')?>&data-state=state_shipping'                            layer.close(index);                        },                        no: function (index) {                            window.location.reload()                            layer.close(index);                        }                    });                    <?php endif;?>                } else if (data.code == 0) {                    layer.open({                        content: data.msg,                        time: 1.5                    })                    window.location.reload()                }            }        })    })</script>